import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

function User() {
  const userData = useSelector((state) => state.user);
  const dispatch = useDispatch();
  const [isEditing, setIsEditing] = useState(false);
  const [editedFirstName, setEditedFirstName] = useState(userData.firstName);
  const [editedLastName, setEditedLastName] = useState(userData.lastName);

  const handleEditButtonClick = () => {
    setIsEditing(true);
  };

  const handleFirstNameChange = (e) => {
    setEditedFirstName(e.target.value);
  };

  const handleLastNameChange = (e) => {
    setEditedLastName(e.target.value);
  };

  const handleInputBlur = async () => {
    setIsEditing(false);
    try {
      const response = await fetch("http://localhost:3001/api/v1/user/update", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          firstName: editedFirstName,
          lastName: editedLastName,
        }),
      });
      if (response.ok) {
        const updatedUserData = await response.json();
        dispatch({ type: "UPDATE_USER_DATA", payload: updatedUserData });
      } else {
        console.error("Erreur lors de la mise à jour utilisateur");
      }
    } catch (error) {
      console.error("Erreur lors de la mise à jour utilisateur");
    }
  };

  return (
    <div className="header">
      <h1>
        Welcome back
        <br />
        {isEditing ? (
          <>
            <input
              type="text"
              value={editedFirstName}
              onChange={handleFirstNameChange}
              onBlur={handleInputBlur}
            />
            <input
              type="text"
              value={editedLastName}
              onChange={handleLastNameChange}
              onBlur={handleInputBlur}
            />
          </>
        ) : (
          <>
            {userData.firstName} {userData.lastName}!
          </>
        )}
      </h1>
      <button className="edit-button" onClick={handleEditButtonClick}>
        Edit Name
      </button>
    </div>
  );
}

export default User;
