import React, { useState, useRef } from "react";

function Signin() {
  const [rememberMe, setRememberMe] = useState(false);
  const passwordInput = useRef();
  const usernameIput = useRef();
  const loginButton = useRef();

  async function loginRequest() {
    let loginForm = {
      email: usernameIput.current.value,
      password: passwordInput.current.value,
    };
    let chargeUtile = JSON.stringify(loginForm);

    return await fetch("http://localhost:3001/api/v1/user/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: chargeUtile,
    }).then((resp) => resp.json());
  }

  function handleLogin(event) {
    event.preventDefault();
    loginRequest().then((response) => {
      if (
        response.error ||
        usernameIput.current.value === "" ||
        passwordInput.current.value === ""
      ) {
        console.log("Echec de connexion");
        animationEchec();
      } else {
        const token = response.token;
        if (rememberMe) {
          localStorage.setItem("token", token);
        } else {
          sessionStorage.setItem("token", token);
        }
        console.log("Connexion Réussie");
      }
    });
  }

  function animationEchec() {
    passwordInput.current.classList.add("loginFailed");
    usernameIput.current.classList.add("loginFailed");
    setTimeout(() => {
      passwordInput.current.classList.remove("loginFailed");
      usernameIput.current.classList.remove("loginFailed");
    }, 500);
  }

  return (
    <main className="main bg-dark">
      <section className="sign-in-content">
        <i className="fa fa-user-circle sign-in-icon"></i>
        <h1>Sign In</h1>
        <form>
          <div className="input-wrapper">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              required="required"
              ref={usernameIput}
            />
          </div>
          <div className="input-wrapper">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              required="required"
              ref={passwordInput}
            />
          </div>
          <div className="input-remember">
            <input
              type="checkbox"
              id="remember-me"
              onChange={(e) => setRememberMe(e.target.checked)}
            />
            <label htmlFor="remember-me">Remember me</label>
          </div>
          <button
            className="sign-in-button"
            id="sign-in-button"
            ref={loginButton}
            onClick={handleLogin}
          >
            Sign In
          </button>
        </form>
      </section>
    </main>
  );
}

export default Signin;
