import React, { useRef, useEffect } from "react";

// Return //

function Signin() {
  // Login Request //

  const passwordInput = useRef();
  const usernameIput = useRef();
  const loginButton = useRef();

  async function loginRequest() {
    /* prettier-ignore */
    let loginForm = {
      "email": usernameIput.current.value,
      "password": passwordInput.current.value,
    };
    let chargeUtile = JSON.stringify(loginForm);

    return await fetch("http://localhost:3001/api/v1/user/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: chargeUtile,
    }).then((resp) => resp.json());
  }

  if (loginButton) {
    loginButton.addEventListener("click", function (event) {
      event.preventDefault();
      loginRequest().then((response) => {
        if (
          response.error ||
          usernameIput.value === "" ||
          passwordInput.value === ""
        ) {
          console.log("Echec de connexion");
          animationEchec();
        } else {
          window.localStorage.setItem("token", response.token);
          console.log("Connexion Reussi");
        }
      });
    });
  }

  function animationEchec() {
    passwordInput.classList.add("loginFailed");
    usernameIput.classList.add("loginFailed");
    window.setTimeout(function () {
      passwordInput.classList.remove("loginFailed");
      usernameIput.classList.remove("loginFailed");
    }, 500);
  }

  return (
    <main className="main bg-dark">
      <section className="sign-in-content">
        <i className="fa fa-user-circle sign-in-icon"></i>
        <h1>Sign In</h1>
        <form>
          <div className="input-wrapper">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              required="required"
              ref={usernameIput}
            />
          </div>
          <div className="input-wrapper">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              required="required"
              ref={passwordInput}
            />
          </div>
          <div className="input-remember">
            <input type="checkbox" id="remember-me" />
            <label htmlFor="remember-me">Remember me</label>
          </div>
          <button
            className="sign-in-button"
            id="sign-in-button"
            ref={loginButton}
          >
            Sign In
          </button>
        </form>
      </section>
    </main>
  );
}

export default Signin;
