import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { updateUserData } from "../../reducers/user.reducer";

function EditUser({ userData }) {
  const [isEditing, setIsEditing] = useState(false);
  const [editedUserName, setEditedUserName] = useState(userData.userName);
  const dispatch = useDispatch();

  useEffect(() => {
    setEditedUserName(userData.userName);
  }, [userData]);

  const handleEditButtonClick = () => {
    setIsEditing(true);
  };

  const handleInputBlur = async () => {
    setIsEditing(false);
    try {
      const response = await fetch("http://localhost:3001/api/v1/user/update", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          userName: editedUserName,
        }),
      });
      if (response.ok) {
        const updatedUserData = await response.json();
        dispatch(updateUserData(updatedUserData));
      } else {
        console.error("Erreur lors de la mise à jour de l'utilisateur");
      }
    } catch (error) {
      console.error("Erreur lors de la mise à jour de l'utilisateur", error);
    }
  };

  const handleInputChange = (e) => {
    setEditedUserName(e.target.value);
  };

  return (
    <div>
      {isEditing ? (
        <div>
          <input
            type="text"
            value={editedUserName}
            onChange={handleInputChange}
            onBlur={handleInputBlur}
            autoFocus
          />
        </div>
      ) : (
        <div>
          <p>{userData.userName}</p>
          <button onClick={handleEditButtonClick}>Edit Name</button>
        </div>
      )}
    </div>
  );
}

export default EditUser;
