import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

function User() {
  const userData = useSelector((state) => state.user);
  const dispatch = useDispatch();
  const [isEditing, setIsEditing] = useState(false);
  const [editedButtonName, setEditedButtonName] = useState("Edit Name");

  const handleEditButtonClick = () => {
    setIsEditing(true);
  };

  const handleButtonNameChange = (e) => {
    setEditedButtonName(e.target.value);
  };

  const handleInputBlur = async () => {
    setIsEditing(false);
    try {
      const response = await fetch("http://localhost:3001/api/v1/user/update", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          userName: editedButtonName,
        }),
      });
      if (response.ok) {
        const updatedUserData = await response.json();
        // Mettre à jour le store Redux avec les données utilisateur mises à jour
        dispatch({ type: "UPDATE_USER_DATA", payload: updatedUserData });
      } else {
        // Gérer les erreurs de mise à jour des données utilisateur
      }
    } catch (error) {
      // Gérer les erreurs
    }
  };

  return (
    <div className="header">
      <h1>
        Welcome back
        <br />
        {userData.firstName} {userData.lastName}!
      </h1>
      {isEditing ? (
        <input
          type="text"
          value={editedButtonName}
          onChange={handleButtonNameChange}
          onBlur={handleInputBlur}
        />
      ) : (
        <button className="edit-button" onClick={handleEditButtonClick}>
          {editedButtonName}
        </button>
      )}
    </div>
  );
}

export default User;
