import React, { useRef } from "react";

function Signin() {
  const passwordInput = useRef(null);
  const usernameInput = useRef(null);

  async function loginRequest() {
    let loginForm = {
      email: usernameInput.current.value,
      password: passwordInput.current.value,
    };
    let chargeUtile = JSON.stringify(loginForm);

    return await fetch("http://localhost:3001/api/v1/user/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: chargeUtile,
    }).then((resp) => resp.json());
  }

  const handleLogin = async (event) => {
    event.preventDefault();
    try {
      const response = await loginRequest();
      if (
        response.error ||
        !usernameInput.current.value ||
        !passwordInput.current.value
      ) {
        console.log("Echec de connexion");
        animationEchec();
      } else {
        window.localStorage.setItem("token", response.token);
        console.log("Connexion Réussie");
      }
    } catch (error) {
      console.error("Erreur lors de la connexion:", error);
      // Gérer les erreurs ici
    }
  };

  function animationEchec() {
    passwordInput.current.classList.add("loginFailed");
    usernameInput.current.classList.add("loginFailed");
    window.setTimeout(function () {
      passwordInput.current.classList.remove("loginFailed");
      usernameInput.current.classList.remove("loginFailed");
    }, 500);
  }

  return (
    <main className="main bg-dark">
      <section className="sign-in-content">
        <i className="fa fa-user-circle sign-in-icon"></i>
        <h1>Sign In</h1>
        <form onSubmit={handleLogin}>
          <div className="input-wrapper">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              required="required"
              ref={usernameInput}
            />
          </div>
          <div className="input-wrapper">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              required="required"
              ref={passwordInput}
            />
          </div>
          <div className="input-remember">
            <input type="checkbox" id="remember-me" />
            <label htmlFor="remember-me">Remember me</label>
          </div>
          <button className="sign-in-button" id="sign-in-button" type="submit">
            Sign In
          </button>
        </form>
      </section>
    </main>
  );
}

export default Signin;
