import { Link } from "react-router-dom";

// Login Request //

const passwordInput = document.getElementById("password");
const usernameIput = document.getElementById("username");
const loginButton = document.getElementById("sign-in-button");
let location = window.document.location.hash;

async function loginRequest() {
  let loginForm = {
    email: usernameIput.value,
    password: passwordInput.value,
  };
  let chargeUtile = JSON.stringify(loginForm);

  return await fetch("http://localhost:3001/api/v1/user/login", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: chargeUtile,
  }).then((resp) => resp.json());
}

loginButton.addEventListener("click", (event) => {
  event.preventDefault();
  loginRequest().then((response) => {
    if (response.error) {
      // alert("Echec de connexion")
      animationEchec();
    } else {
      window.localStorage.setItem("token", response.token);
      location = "#user";
    }
  });
});

function animationEchec() {
  passwordInput.classList.add("loginFailed");
  usernameIput.classList.add("loginFailed");
  window.setTimeout(function () {
    passwordInput.classList.remove("loginFailed");
    usernameIput.classList.remove("loginFailed");
  }, 500);
}

// Return //

function Signin() {
  return (
    <main className="main bg-dark">
      <section className="sign-in-content">
        <i className="fa fa-user-circle sign-in-icon"></i>
        <h1>Sign In</h1>
        <form>
          <div className="input-wrapper">
            <label htmlFor="username">Username</label>
            <input type="text" id="username" required="required" />
          </div>
          <div className="input-wrapper">
            <label htmlFor="password">Password</label>
            <input type="password" id="password" required="required" />
          </div>
          <div className="input-remember">
            <input type="checkbox" id="remember-me" />
            <label htmlFor="remember-me">Remember me</label>
          </div>
          <button
            class="sign-in-button"
            id="sign-in-button"
            onClick={() => {
              window.document.location.hash = "#user";
            }}
          >
            Sign In
          </button>
        </form>
      </section>
    </main>
  );
}

export default Signin;
