import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { updateUserData } from "../reducers/";

function EditUser({ userData }) {
  const [editedFirstName, setEditedFirstName] = useState("");
  const [editedLastName, setEditedLastName] = useState("");
  const dispatch = useDispatch();

  useEffect(() => {
    setEditedFirstName(userData.firstName);
    setEditedLastName(userData.lastName);
  }, [userData]);

  const handleFirstNameChange = (e) => {
    setEditedFirstName(e.target.value);
  };

  const handleLastNameChange = (e) => {
    setEditedLastName(e.target.value);
  };

  const handleInputBlur = async () => {
    try {
      const response = await fetch("http://localhost:3001/api/v1/user/update", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          firstName: editedFirstName,
          lastName: editedLastName,
        }),
      });
      if (response.ok) {
        const updatedUserData = await response.json();
        dispatch(updateUserData(updatedUserData));
      } else {
        console.error("Erreur lors de la mise à jour utilisateur");
      }
    } catch (error) {
      console.error("Erreur lors de la mise à jour utilisateur");
    }
  };

  return (
    <div>
      <h1>Edit User</h1>
      <div>
        <label htmlFor="firstName">First Name:</label>
        <input
          type="text"
          id="firstName"
          value={editedFirstName}
          onChange={handleFirstNameChange}
          onBlur={handleInputBlur}
        />
      </div>
      <div>
        <label htmlFor="lastName">Last Name:</label>
        <input
          type="text"
          id="lastName"
          value={editedLastName}
          onChange={handleLastNameChange}
          onBlur={handleInputBlur}
        />
      </div>
    </div>
  );
}

export default EditUser;
