import data from "../../datas/accounts.json";

function Account(name, solde, balance) {
  let token = "";
  const getToken = () => {
    token = window.sessionStorage.getItem("token");
    if (!token) {
      token = window.localStorage.getItem("token");
    }
    return token;
  };
  getToken();

  let accounts = [];
  const getAccounts = () => {
    data.usersAccounts.forEach((userAccount) => {
      if (userAccount.id === token) {
        accounts.push(...userAccount.accounts);
      }
    });
    return accounts;
  };
  getAccounts();

  return (
    <section className="account">
      <div className="account-content-wrapper">
        <h3 className="account-title">{name}</h3>
        <p className="account-amount">${solde}</p>
        <p className="account-amount-description">{balance}</p>
      </div>
      <div className="account-content-wrapper cta">
        <button className="transaction-button">View transactions</button>
      </div>
    </section>
  );
}

export default Account;
