import React, { useState, useRef, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";

function Signin() {
  const [rememberMe, setRememberMe] = useState(false);
  const passwordInput = useRef();
  const usernameInput = useRef();
  const loginButton = useRef();
  const isLoggedIn = useSelector((state) => state.log.isLoggedIn);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    if (isLoggedIn) {
      navigate("/user");
    }
  }, [isLoggedIn, navigate]);

  async function loginRequest() {
    let loginForm = {
      email: usernameInput.current.value,
      password: passwordInput.current.value,
    };
    let chargeUtile = JSON.stringify(loginForm);

    try {
      const response = await fetch("http://localhost:3001/api/v1/user/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: chargeUtile,
      });
      const data = await response.json();
      if (response.ok) {
        const token = data.body.token;
        sessionStorage.setItem("token", token);
        if (rememberMe) {
          localStorage.setItem("token", token);
        }
        dispatch({ type: "LOGIN_SUCCESS" });
      } else {
        console.log("Echec de connexion");
        animationEchec();
      }
    } catch (error) {
      console.log("Erreur lors de la connexion :", error);
      animationEchec();
    }
  }

  const handleLogin = async (event) => {
    event.preventDefault();
    await loginRequest();
  };

  function animationEchec() {
    passwordInput.current.classList.add("loginFailed");
    usernameInput.current.classList.add("loginFailed");
    window.setTimeout(function () {
      passwordInput.current.classList.remove("loginFailed");
      usernameInput.current.classList.remove("loginFailed");
    }, 500);
  }

  return (
    <main className="main bg-dark">
      <section className="sign-in-content">
        <i className="fa fa-user-circle sign-in-icon"></i>
        <h1>Sign In</h1>
        <form onSubmit={handleLogin}>
          <div className="input-wrapper">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              required="required"
              ref={usernameInput}
            />
          </div>
          <div className="input-wrapper">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              required="required"
              ref={passwordInput}
            />
          </div>
          <div className="input-remember">
            <input
              type="checkbox"
              id="remember-me"
              onChange={(e) => setRememberMe(e.target.checked)}
            />
            <label htmlFor="remember-me">Remember me</label>
          </div>
          <button
            className="sign-in-button"
            id="sign-in-button"
            ref={loginButton}
            onClick={handleLogin}
          >
            Sign In
          </button>
        </form>
      </section>
    </main>
  );
}

export default Signin;
